<?php

namespace App\Repository;

/**
 * PageRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PageRepository extends \Doctrine\ORM\EntityRepository
{
    public function pagesPublieesQB(){
        return $this
            ->createQueryBuilder('p')
                ->where('p.corbeille = 0')
                ->andWhere('p.active = 1')
                //->andWhere('p.langue = :langue')
                //->setParameter('langue', $langue)
                ->orderBy('p.datePublication', 'DESC');
    }

    public function pagesPubliees($langue = null, $limite = null, $offset = null){
        $timestamp = new \DateTime();
        $date = $timestamp->format('Y-m-d H:i:s');

        $qb = $this
            ->createQueryBuilder('p')
                ->where('p.corbeille = 0')
                ->andWhere('p.active = 1')
                ->andWhere('p.datePublication < :date')
                ->andWhere('p.dateDepublication > :date OR p.dateDepublication IS NULL');

        if($langue){
            $qb->andWhere('p.langue = :langue')
                ->setParameters(array('langue' => $langue, 'date' => $date));
        }else{
            $qb->setParameters(array('date' => $date));
        }

        $qb->orderBy('p.datePublication', 'DESC');

        if($limite){
            $qb->setMaxResults($limite);
        }

        if($offset){
            $qb->setFirstResult($offset);
        }

        return $qb->getQuery()->getResult();
    }

    //Pour le bloc catÃ©gorie
    public function pagesPublieesCategorie($categorie, $langue, $limite = null, $tri = null){
        $timestamp = new \DateTime();
        $date = $timestamp->format('Y-m-d H:i:s');

        $qb = $this
            ->createQueryBuilder('p')
            ->join('p.categories', 'c');

        $qb->where('p.corbeille = 0')
            ->andWhere('p.active = 1')
            ->andWhere('p.langue = :langue')
            ->andWhere('p.datePublication < :date')
            ->andWhere('p.dateDepublication > :date OR p.dateDepublication IS NULL');

        if($categorie != 0){
            $qb->andWhere(':categorie MEMBER OF p.categories');
            $qb->setParameters(array('langue' => $langue, 'date' => $date, 'categorie' => $categorie));
        }else{
            $qb->andWhere('c IS NOT NULL');
            $qb->setParameters(array('langue' => $langue, 'date' => $date));
        }

        if($tri == 'alpha'){
            $qb->orderBy('p.titre', 'ASC');
        }else{
            $qb->orderBy('p.datePublication', 'DESC');
        }

        if($limite){
            $qb->setMaxResults($limite);
        }

        return $qb->getQuery()->getResult();
    }

    public function nombreTotal(){
        $qb = $this
            ->createQueryBuilder('p')
            ->select('count(p.id)');

        return $qb->getQuery()->getSingleScalarResult();
    }

    public function nombrePagesPubliees($langue = null, $limite = null){
        $timestamp = new \DateTime();
        $date = $timestamp->format('Y-m-d H:i:s');

        $qb = $this
            ->createQueryBuilder('p')
            ->select('count(p.id)')
            ->where('p.corbeille = 0')
            ->andWhere('p.active = 1')
            ->andWhere('p.datePublication < :date')
            ->andWhere('p.dateDepublication > :date OR p.dateDepublication IS NULL');

        if($langue){
            $qb->andWhere('p.langue = :langue')
                ->setParameters(array('langue' => $langue, 'date' => $date));
        }else{
            $qb->setParameters(array('date' => $date));
        }

        $nbResultats = $qb->getQuery()->getSingleScalarResult();

        if($limite && $limite < $nbResultats){
            return $limite;
        }else{
            return $nbResultats;
        }
    }

    public function titreMenuLike($langue, $recherche){
        $qb = $this
            ->createQueryBuilder('p')
            ->where('p.langue = :langue')
            ->andwhere('p.titreMenu LIKE :recherche')
            ->setParameters(array('langue' => $langue, 'recherche' => '%'.$recherche.'%'));

        return $qb->getQuery()->getResult();
    }

    public function recherche($motsCles){
        $resultats = [];

        foreach($motsCles as $motCle){
            $qb = $this
                ->createQueryBuilder('p')
                ->where('p.titre LIKE :motCle')
                ->orWhere('p.titreMenu LIKE :motCle')
                ->setParameters(array('motCle' => '%'.$motCle.'%'));

            $pages = $qb->getQuery()->getResult();
            foreach($pages as $page){
                $resultats['page'.$page->getId()] = $page;
            }
        }

        return $resultats;
    }
}
